#include "exa/MakeExample.h"

#include <tcl.h>

#include "exa/example.h"
#include "utl/decode.h"

// Generated by swig from the example.i
extern "C" {
extern int Exa_Init(Tcl_Interp* interp);
}

namespace exa {

// example.tcl encoded as strings.
extern const char* exa_tcl_inits[];

// This makes an uninitialized example.  It won't be ready to use
// until initExample is called on it.
exa::Example* makeExample()
{
  return new Example;
}

void deleteExample(exa::Example* example)
{
  delete example;
}

void initExample(exa::Example* example,
                 odb::dbDatabase* db,
                 utl::Logger* logger,
                 Tcl_Interp* tcl_interp)
{
  // Define swig TCL commands.
  Exa_Init(tcl_interp);
  // Eval encoded TCL sources.
  utl::evalTclInit(tcl_interp, exa_tcl_inits);
  // Initialize example
  example->init(db, logger);
}

}  // namespace exa
